#page-toolbar {
    border-radius:0;
    right: 12px;
    left: 12px;
    width:auto;
    height:480px;
}

#page-toolbar .pages .availablepage {
    margin-right:0;
    height:132px;
    width:107px;
}

#page-toolbar .pages .availablepage span {
    font-size: .85em;
    overflow: visible;
    line-height: 1.25em;
    top:90px;
    bottom:auto;
}

#page-toolbar .container {
    -webkit-box-shadow: 0px 0px 15px 2px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 0px 15px 2px rgba(0,0,0,0.75);
    box-shadow: 0px 0px 15px 2px rgba(0,0,0,0.75);
    white-space:normal;
    height:480px;
}

#page-toolbar .handle {
    opacity:.33;   
}

#rightsidebar ul.tabmenu {
    top:0; 
}

#page-toolbar .pages .availablepage img.pagethumb {
    max-width:70px;
}

#radial-menu .markermenu.open { height:274px; padding-top:14px; }
#radial-menu .markermenu .markericon.active { border-color:#6da4d8; border-radius:4px; }
#radial-menu .markermenu.open::before { content:attr(data-search); position:absolute; top:4px; left:50%; transform:translateX(-50%); font-weight:bold; text-align:center; }

.dialog label { padding-right:0; }
.ui-dialog { max-height:90vh; }
.ui-dialog .characterdialog { display:table !important; width:calc(100% - 50px) !important; }
.ui-dialog .characterdialog .tab-content { overflow: auto; position:relative; height: calc(100% - 50px); }
.characterdialog .characterviewer { display:table-cell !important;  }

98c70849-ce63-d563-f142-b41c97ac5ca7.jsContent:
$("#page-toolbar").css("top", "-480px");


    const slider = $("#zoomslider");
    const pagesDisplay = $(".pages.ui-sortable");



(function(){
    const iconEffects = {
        "slow": "toggle_status_snail",
        "prone": "toggle_status_back-pain",
        "fear": "toggle_status_sleepy",
        "mage armor": "toggle_status_aura"
    };


    let markerMenuString = "";
    let markerMenuTimeout = null;


    $(document).keydown(function(e){
        const markerMenu = $("#radial-menu .markermenu.open");
        const markerMenuSearchTimeout = 1100;

        /**
         * If the marker menu is showing, capture alpha key inputs to handle quick searching
         * for status effect icons.
         */
        if(markerMenu.length > 0){
            e.preventDefault();

            if((e.keyCode >= 65 && e.keyCode <= 90)
                || e.keyCode === 32 || e.keyCode === 8 || e.keyCode === 13){

                if(e.keyCode === 13){
                    if(markerMenuString !== ""){
                        for(let i in iconEffects){
                            if(i.substring(0, markerMenuString.length).toLowerCase() === markerMenuString){
                                markerMenu.find(".markericon[data-action-type='" + iconEffects[i] + "']").trigger("click");
                            }
                        }
                    }
                }
                else{
                    clearTimeout(markerMenuTimeout);
                    if(e.keyCode === 8){
                        markerMenuString = markerMenuString.slice(0, -1);
                    }
                    else{
                        markerMenuString += String.fromCharCode(e.keyCode).toLowerCase();
                    }
                    markerMenu.attr("data-search", markerMenuString);
                    markerMenuTimeout = setTimeout(function(){
                        markerMenuString = "";
                        markerMenu.attr("data-search", markerMenuString);
                        markerMenu.find(".markericon").show();
                    }, markerMenuSearchTimeout);

                    markerMenu.find(".markericon").hide();

                    for(let i in iconEffects){
                        if(i.substring(0, markerMenuString.length).toLowerCase() === markerMenuString){
                            markerMenu.find(".markericon[data-action-type='" + iconEffects[i] + "']").show();
                        }
                    }
                }
            }

            return;
        }

        if(e.ctrlKey){
            switch(e.keyCode){
                case 187:
                    e.preventDefault();
                    slider.slider("option", "value", parseInt(slider.slider("option", "value")) + 10);
                    slider.slider("option").slide(null, {value: slider.slider("option", "value")});
                    break;
                case 189:   
                    e.preventDefault();
                    slider.slider("option", "value", parseInt(slider.slider("option", "value")) - 10);
                    slider.slider("option").slide(null, {value: slider.slider("option", "value")});
                    break;
                case 76:    // L
                    $("#editinglayer li.choosewalls").trigger("click");
                    break;
            }

        }
    })


})();
